import { motion, useAnimation } from 'framer-motion';
import { useEffect } from 'react';

const RoadmapAirplane = ({ currentSkill, skills }) => {
  const controls = useAnimation();

  // Calculate position based on current skill
  const getPosition = (skillId) => {
    const index = skills.findIndex(s => s.id === skillId);
    return index * 100; // 100px per house
  };

  useEffect(() => {
    // Animate to the current skill position
    controls.start({
      x: getPosition(currentSkill),
      transition: { duration: 1, ease: "easeInOut" }
    });
  }, [currentSkill, controls]);

  return (
    <div className="roadmap-container">
      {/* Road Background */}
      <div className="road" style={{ width: `${(skills.length - 1) * 100}px` }}></div>

      {/* Skill Houses */}
      {skills.map((skill, index) => (
        <div 
          key={skill.id} 
          className={`skill-house ${currentSkill === skill.id ? 'active' : ''}`}
          style={{ left: `${index * 100}px` }}
        >
          <div className="house-icon">{skill.icon}</div>
          <div className="skill-name">{skill.name}</div>
        </div>
      ))}

      {/* Airplane */}
      <motion.div 
        className="airplane"
        animate={controls}
        initial={{ x: getPosition(currentSkill) }}
      >
        ✈️
      </motion.div>
    </div>
  );
};

export default RoadmapAirplane;
