      {/* Features Section */}
      <section className="features-section">
        <div className="section-container">
          <h2 className="section-title">Why Learn With Us</h2>
          <p className="section-subtitle">
            We combine the best of structured education with practical applications
          </p>

          <div className="features-grid">
            {features.map((feature, index) => (
              <div key={index} className="feature-card" role="region" aria-label={feature.title}>
                <div className="feature-icon">{feature.icon}</div>
                <h3 className="feature-title">{feature.title}</h3>
                <p className="feature-description">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Roles Section */}
      <section className="roles-section">
        <div className="section-container">
          <h2>Explore Career Roles</h2>
          <p className="section-subtitle">
            Choose your path based on popular roles in data and technology.
          </p>

          <div className="roles-grid">
            {roles.map((role) => (
              <article key={role.id} className={`role-card ${role.category.toLowerCase().replace(' ', '-')} ${role.popular ? 'popular' : ''}`}>
                <header className="role-header">
                  <div className="role-icon">{role.icon}</div>
                  <h3 className="role-title">{role.title}</h3>
                  <p className="role-category">{role.category}</p>
                </header>
                <div className="role-content">
                  <p className="role-description">
                    {role.description}
                  </p>

                  <div className="role-stats">
                    <div className="stat-item">
                      <div className="stat-value">{role.stats.salary}</div>
                      <div className="stat-label">Average Salary</div>
                    </div>
                    <div className="stat-item">
                      <div className="stat-value">{role.stats.experience}</div>
                      <div className="stat-label">Experience</div>
                    </div>
                    <div className="stat-item">
                      <div className="stat-value">{role.stats.openings}</div>
                      <div className="stat-label">Openings</div>
                    </div>
                  </div>

                  <div className="exam-selection">
                    <label className="exam-label" htmlFor={`progress-${role.id}`}>Skill Progress</label>
                    <div className="exam-bar">
                      <div 
                        id={`progress-${role.id}`} 
                        className="exam-progress-bar" 
                        style={{ width: `${role.progress}%` }}
                      ></div>
                    </div>
                    <div className="exam-info">
                      <span>{role.progress}%</span>
                      <span>
                        {role.progress >= 70 ? 'Ready to Start' : 
                         role.progress >= 50 ? 'Keep Practicing' : 
                         'Start Learning'}
                      </span>
                    </div>
                    <button className="start-exam-button">Start Exam</button>
                  </div>
                </div>
              </article>
            ))}
          </div>
        </div>
      </section>
    </div>
