import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { motion } from 'framer-motion';
import roleSkillsData from '../utils/roleSkillsData';
import './SkillPage.css';

const SkillPage = () => {
  const { roleSlug } = useParams();
  const history = useHistory();
  const [roleData, setRoleData] = useState(null);
  const [activeCategory, setActiveCategory] = useState(null);
  const [expandedSkill, setExpandedSkill] = useState(null);

  useEffect(() => {
    const data = roleSkillsData[roleSlug];
    if (data) {
      setRoleData(data);
      setActiveCategory(Object.keys(data.skills)[0]);
    }
  }, [roleSlug]);

  if (!roleData) {
    return (
      <div className="skill-page-loading">
        <div className="loading-spinner"></div>
        <p>Loading skills...</p>
      </div>
    );
  }

  const getImportanceColor = (importance) => {
    switch (importance) {
      case 'Essential':
        return '#ef4444';
      case 'Important':
        return '#f59e0b';
      case 'Useful':
        return '#10b981';
      default:
        return '#6b7280';
    }
  };

  const getLevelColor = (level) => {
    switch (level) {
      case 'Advanced':
        return '#8b5cf6';
      case 'Intermediate':
        return '#06b6d4';
      case 'Beginner':
        return '#10b981';
      default:
        return '#6b7280';
    }
  };

  const toggleSkillExpand = (skillName) => {
    setExpandedSkill(expandedSkill === skillName ? null : skillName);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  const skillCardVariants = {
    collapsed: { height: 160 },
    expanded: { height: 'auto' }
  };

  return (
    <div 
      className="skill-page"
      style={{
        '--role-color': roleData.color,
        '--role-light-color': `${roleData.color}20`,
        '--role-gradient': roleData.gradient
      }}
    >
      {/* Hero Section */}
      <motion.div 
        className="skill-hero"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <div className="hero-background">
          <div className="gradient-overlay"></div>
          <div className="pattern-overlay"></div>
        </div>

        <div className="hero-content">
          <motion.button 
            className="back-button"
            onClick={() => history.goBack()}
            aria-label="Go back"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
              <path d="M19 12H5M12 19L5 12L12 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            Back to Roles
          </motion.button>

          <div className="role-hero-info">
            <motion.div 
              className="role-icon-container"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ delay: 0.2, duration: 0.5 }}
            >
              <div className="role-icon-large">{roleData.icon}</div>
            </motion.div>

            <div className="role-hero-text">
              <motion.h1 
                className="role-title"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.3, duration: 0.5 }}
              >
                {roleData.title} Skills
              </motion.h1>

              <motion.p 
                className="role-description"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.4, duration: 0.5 }}
              >
                {roleData.description}
              </motion.p>

              <motion.div 
                className="role-stats"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.5 }}
              >
                <div className="stat-item">
                  <span className="stat-number">
                    {Object.values(roleData.skills).flat().length}
                  </span>
                  <span className="stat-label">Total Skills</span>
                </div>
                <div className="stat-item">
                  <span className="stat-number">
                    {Object.values(roleData.skills).flat().filter(s => s.importance === 'Essential').length}
                  </span>
                  <span className="stat-label">Core Skills</span>
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <div className="skill-main-content">
        {/* Categories Navigation */}
        <motion.div 
          className="categories-nav-container"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.5 }}
        >
          <div className="categories-nav">
            {Object.keys(roleData.skills).map((category) => (
              <button
                key={category}
                className={`category-nav-item ${activeCategory === category ? 'active' : ''}`}
                onClick={() => {
                  setActiveCategory(category);
                  window.scrollTo({ top: 400, behavior: 'smooth' });
                }}
              >
                <span className="category-name">{category}</span>
                <span className="category-indicator"></span>
              </button>
            ))}
          </div>
        </motion.div>

        {/* Skills Section */}
        <div className="skills-section">
          <motion.div 
            className="category-header"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <div className="category-tag">Skill Category</div>
            <h2 className="category-title">{activeCategory}</h2>
            <p className="category-description">
              These are the key skills needed for {activeCategory?.toLowerCase()} as a {roleData.title}
            </p>
          </motion.div>

          <motion.div 
            className="skills-grid"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {activeCategory && roleData.skills[activeCategory].map((skill) => (
              <motion.div
                key={skill.name}
                className={`skill-card ${expandedSkill === skill.name ? 'expanded' : ''}`}
                variants={itemVariants}
                whileHover={{ y: -5 }}
                animate={expandedSkill === skill.name ? 'expanded' : 'collapsed'}
                variants={skillCardVariants}
                transition={{ duration: 0.3 }}
              >
                <div 
                  className="skill-card-header"
                  onClick={() => toggleSkillExpand(skill.name)}
                >
                  <div className="skill-icon">{skill.icon}</div>
                  <div className="skill-info">
                    <h3 className="skill-name">{skill.name}</h3>
                    <div className="skill-meta">
                      <span 
                        className="skill-level"
                        style={{ color: getLevelColor(skill.level) }}
                      >
                        {skill.level}
                      </span>
                      <span className="skill-separator">•</span>
                      <span 
                        className="skill-importance"
                        style={{ color: getImportanceColor(skill.importance) }}
                      >
                        {skill.importance}
                      </span>
                    </div>
                  </div>
                  <motion.div 
                    className="expand-icon"
                    animate={{ rotate: expandedSkill === skill.name ? 180 : 0 }}
                  >
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                      <path d="M19 9L12 16L5 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </motion.div>
                </div>

                <div className="skill-card-content">
                  <p className="skill-description">{skill.description}</p>

                  <div className="skill-progress-container">
                    <div className="progress-labels">
                      <span>Beginner</span>
                      <span>Intermediate</span>
                      <span>Advanced</span>
                    </div>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill"
                        style={{ 
                          width: skill.level === 'Advanced' ? '90%' : 
                                 skill.level === 'Intermediate' ? '60%' : '30%',
                          background: roleData.gradient
                        }}
                      ></div>
                    </div>
                  </div>

                  {skill.resources && (
                    <div className="skill-resources">
                      <h4>Learning Resources</h4>
                      <div className="resource-links">
                        {skill.resources.map((resource, index) => (
                          <a 
                            key={index} 
                            href={resource.url} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="resource-link"
                          >
                            {resource.name}
                          </a>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Call to Action */}
        <motion.div 
          className="cta-section"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true, margin: "0px 0px -100px 0px" }}
        >
          <div className="cta-content">
            <h3>Ready to master these skills?</h3>
            <p>Start your personalized learning path tailored for {roleData.title} role</p>
            <div className="cta-buttons">
              <motion.button 
                className="cta-primary"
                style={{ background: roleData.gradient }}
                whileHover={{ scale: 1.03 }}
                whileTap={{ scale: 0.98 }}
              >
                Begin Learning Journey
              </motion.button>
              <motion.button 
                className="cta-secondary"
                whileHover={{ scale: 1.03 }}
                whileTap={{ scale: 0.98 }}
              >
                Download Skills Checklist
              </motion.button>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default SkillPage;
