import { useEffect, useState } from 'react';

const RoadmapAirplane = () => {
  const [skills, setSkills] = useState([
    { id: 1, name: 'HTML', completed: true },
    { id: 2, name: 'CSS', completed: true },
    { id: 3, name: 'SQL', completed: false },
    { id: 4, name: 'Excel', completed: false },
    { id: 5, name: 'Python', completed: false }
  ]);

  // Load progress from localStorage on component mount
  useEffect(() => {
    const savedProgress = localStorage.getItem('skillsProgress');
    if (savedProgress) {
      setSkills(JSON.parse(savedProgress));
    }
  }, []);

  // Save progress to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('skillsProgress', JSON.stringify(skills));
  }, [skills]);

  const completeSkill = (skillId) => {
    const updatedSkills = skills.map(skill => 
      skill.id === skillId ? { ...skill, completed: true } : skill
    );
    setSkills(updatedSkills);
    
    // Find next incomplete skill
    const nextSkill = skills.find(s => s.id > skillId && !s.completed);
    if (nextSkill) {
      setTimeout(() => {
        alert(`Great job completing ${skills.find(s => s.id === skillId).name}! Let's get started with ${nextSkill.name}!`);
      }, 1000);
    }
  };

  // Calculate airplane position
  const completedCount = skills.filter(s => s.completed).length;
  const airplanePosition = completedCount > 0 
    ? (completedCount / skills.length) * 100 
    : 5; // Default starting position

  return (
    <div className="roadmap-container">
      <h2 className="text-2xl font-bold mb-8">My Learning Journey</h2>
      
      <div className="relative h-64 w-full max-w-4xl mx-auto">
        {/* Road */}
        <div className="absolute top-1/2 left-0 w-full h-2 bg-gray-500 rounded-full"></div>
        
        {/* Airplane */}
        <div 
          className="absolute top-1/2 -translate-y-1/2 w-16 h-16 transition-all duration-1000 ease-in-out"
          style={{ left: `${airplanePosition}%`, transform: 'translate(-50%, -100%)' }}
        >
          <img src={airplaneImg} alt="Progress airplane" className="w-full h-full" />
        </div>
        
        {/* Skill Houses */}
        {skills.map((skill, index) => (
          <div 
            key={skill.id}
            className="absolute top-1/2 -translate-y-1/2"
            style={{ 
              left: `${(index / (skills.length - 1)) * 100}%`,
              transform: 'translateX(-50%)'
            }}
          >
            <div 
              className={`w-12 h-8 mx-auto rounded-t-md relative cursor-pointer
                ${skill.completed ? 'bg-green-500' : 'bg-orange-400'}`}
              onClick={() => !skill.completed && completeSkill(skill.id)}
            >
              {/* Roof */}
              <div className={`absolute -top-2 left-0 w-0 h-0 
                border-l-[24px] border-l-transparent
                border-b-[16px] ${skill.completed ? 'border-b-green-700' : 'border-b-orange-600'}
                border-r-[24px] border-r-transparent`}>
              </div>
            </div>
            <span className="block text-center mt-2 font-medium">
              {skill.name}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoadmapAirplane;
