import React from "react";
import { useParams } from "react-router-dom";
import SimpleRoadmapAirplane from "../components/SimpleRoadmapAirplane";

const Roadmap = () => {
  const { roleSlug } = useParams();

  // If we have a roleSlug, show the roadmap for that role
  if (roleSlug) {
    return <SimpleRoadmapAirplane />;
  }

  // Default roadmap page when no role is selected
  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #f0f9ff 0%, #ffffff 50%, #faf5ff 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{ textAlign: 'center', maxWidth: '600px', padding: '32px' }}>
        <h1 style={{ fontSize: '48px', marginBottom: '16px' }}>üõ©Ô∏è</h1>
        <h2 style={{ fontSize: '32px', fontWeight: 'bold', color: '#1f2937', marginBottom: '16px' }}>
          Choose Your Learning Path
        </h2>
        <p style={{ fontSize: '18px', color: '#6b7280', marginBottom: '32px' }}>
          Select a data science role from the home page to start your personalized learning journey with our interactive roadmap.
        </p>
        <button
          onClick={() => window.location.href = '/home'}
          style={{
            padding: '12px 24px',
            background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: 'pointer',
            boxShadow: '0 4px 12px rgba(99, 102, 241, 0.3)'
          }}
        >
          Explore Roles
        </button>
      </div>
    </div>
  );
};

export default Roadmap;
