import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useHistory } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const [theme, setTheme] = useState('dark');
  const history = useHistory();

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'dark' ? 'light' : 'dark'));
  };

  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);

  const handleStartLearning = () => {
    history.push('/signup');
  };

  const handleGetStarted = () => {
    history.push('/signup');
  };

  const handleSignIn = () => {
    history.push('/signin');
  };

  const handleSignUp = () => {
    history.push('/signup');
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.2 },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.6 } },
  };

  return (
    <div className={`landing-page ${theme}-theme`}>
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-container">
          {/* Logo */}
          <div className="navbar-logo">
            <div className="logo-container">
              <div className="logo-icon">
                <svg width="40" height="40" viewBox="0 0 40 40" fill="none">
                  <defs>
                    <linearGradient id="logoGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                      <stop offset="0%" stopColor="#6366f1" />
                      <stop offset="50%" stopColor="#8b5cf6" />
                      <stop offset="100%" stopColor="#06b6d4" />
                    </linearGradient>
                  </defs>
                  <circle cx="20" cy="20" r="18" fill="url(#logoGradient)" fillOpacity="0.1" stroke="url(#logoGradient)" strokeWidth="2"/>
                  <path d="M12 16L20 24L28 16" stroke="url(#logoGradient)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                  <circle cx="15" cy="12" r="2" fill="url(#logoGradient)"/>
                  <circle cx="25" cy="12" r="2" fill="url(#logoGradient)"/>
                  <circle cx="20" cy="28" r="2" fill="url(#logoGradient)"/>
                  <path d="M8 20H12M28 20H32" stroke="url(#logoGradient)" strokeWidth="2" strokeLinecap="round"/>
                </svg>
              </div>
              <div className="logo-text">
                <span className="logo-name">PR Learning</span>
                <span className="logo-tagline">Platform</span>
              </div>
            </div>
          </div>

          {/* Navigation Buttons */}
          <div className="navbar-buttons">
            <button className="nav-button theme-toggle-btn" onClick={toggleTheme}>
              {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
            </button>
            <button className="nav-button signin-btn" onClick={handleSignIn}>
              Sign In
            </button>
            <button className="nav-button signup-btn" onClick={handleSignUp}>
              Sign Up
            </button>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-background">
          <div className="data-science-bg-image"></div>
          <div className="gradient-circle blue"></div>
          <div className="gradient-circle teal"></div>
          <div className="gradient-circle purple"></div>

          {/* Moving Data Science Background Elements */}
          <div className="data-science-background">
            <motion.div 
              className="moving-element element-1"
              animate={{ 
                x: [0, 100, 0],
                y: [0, -50, 0],
                rotate: [0, 10, 0]
              }}
              transition={{ 
                duration: 20, 
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              <div className="ds-icon">üìä</div>
            </motion.div>

            <motion.div 
              className="moving-element element-2"
              animate={{ 
                x: [0, -80, 0],
                y: [0, 60, 0],
                rotate: [0, -15, 0]
              }}
              transition={{ 
                duration: 18, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 2
              }}
            >
              <div className="ds-icon">ü§ñ</div>
            </motion.div>

            <motion.div 
              className="moving-element element-3"
              animate={{ 
                x: [0, 120, 0],
                y: [0, 40, 0],
                rotate: [0, 8, 0]
              }}
              transition={{ 
                duration: 22, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 1
              }}
            >
              <div className="ds-icon">üìà</div>
            </motion.div>

            <motion.div 
              className="moving-element element-4"
              animate={{ 
                x: [0, -60, 0],
                y: [0, -70, 0],
                rotate: [0, -12, 0]
              }}
              transition={{ 
                duration: 16, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 3
              }}
            >
              <div className="ds-icon">üß†</div>
            </motion.div>

            <motion.div 
              className="moving-element element-5"
              animate={{ 
                x: [0, 70, 0],
                y: [0, -30, 0],
                rotate: [0, 5, 0]
              }}
              transition={{ 
                duration: 19, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 4
              }}
            >
              <div className="ds-icon">üîç</div>
            </motion.div>

            <motion.div 
              className="moving-element element-6"
              animate={{ 
                x: [0, -100, 0],
                y: [0, 20, 0],
                rotate: [0, -8, 0]
              }}
              transition={{ 
                duration: 21, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 5
              }}
            >
              <div className="ds-icon">üíª</div>
            </motion.div>

            {/* Data points floating around */}
            {[...Array(12)].map((_, i) => (
              <motion.div
                key={i}
                className={`data-point point-${i + 1}`}
                animate={{
                  y: [0, -20, 0],
                  opacity: [0.3, 0.8, 0.3]
                }}
                transition={{
                  duration: 3 + i * 0.5,
                  repeat: Infinity,
                  delay: i * 0.3
                }}
              />
            ))}
          </div>
        </div>

        <div className="hero-content-container">
          <motion.div 
            className="hero-content" 
            initial="hidden" 
            animate="visible" 
            variants={containerVariants}
          >
            <motion.h1 variants={itemVariants} className="hero-title">
              Master Data Science
            </motion.h1>

            <motion.h2 variants={itemVariants} className="hero-subtitle">
              Transform Your Future
            </motion.h2>

            <motion.p variants={itemVariants} className="hero-description">
              Learn Python, Machine Learning, AI, and Data Analytics through hands-on projects.
              Build real-world skills that employers demand.
            </motion.p>

            <motion.div variants={itemVariants} className="hero-cta">
              <button className="primary-button" onClick={handleStartLearning}>Start Learning Free</button>
              <button className="secondary-button">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" stroke="currentColor" strokeWidth="2"/>
                  <path d="M10 8L16 12L10 16V8Z" stroke="currentColor" strokeWidth="2"/>
                </svg>
                Watch Learning Path
              </button>
            </motion.div>

            {/* Stats Section */}
            <motion.div 
              variants={itemVariants} 
              className="stats-container"
            >
              <div className="stat-item">
                <div className="stat-value">10K+</div>
                <div className="stat-label">Students</div>
              </div>
              <div className="stat-item">
                <div className="stat-value">200+</div>
                <div className="stat-label">Courses</div>
              </div>
              <div className="stat-item">
                <div className="stat-value">95%</div>
                <div className="stat-label">Success Rate</div>
              </div>
            </motion.div>
          </motion.div>

          {/* Hero Visual Section */}
          <div className="hero-visual">
            <div className="data-science-visual">
              {/* Main Dashboard Mockup */}
              <motion.div 
                className="dashboard-mockup"
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.3 }}
              >
                <div className="mockup-header">
                  <div className="mockup-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                  <div className="mockup-title">Data Analytics Dashboard</div>
                </div>
                <div className="mockup-content">
                  <div className="chart-container">
                    <div className="chart-bars">
                      <div className="bar" style={{height: '60%'}}></div>
                      <div className="bar" style={{height: '80%'}}></div>
                      <div className="bar" style={{height: '45%'}}></div>
                      <div className="bar" style={{height: '90%'}}></div>
                      <div className="bar" style={{height: '70%'}}></div>
                    </div>
                  </div>
                  <div className="stats-mini">
                    <div className="stat-mini">
                      <div className="stat-icon">üìä</div>
                      <div className="stat-info">
                        <div className="stat-number">2.4K</div>
                        <div className="stat-text">Models</div>
                      </div>
                    </div>
                    <div className="stat-mini">
                      <div className="stat-icon">ü§ñ</div>
                      <div className="stat-info">
                        <div className="stat-number">89%</div>
                        <div className="stat-text">Accuracy</div>
                      </div>
                    </div>
                  </div>
                </div>
              </motion.div>

              {/* Floating Code Snippet */}
              <motion.div 
                className="code-snippet"
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8, delay: 0.5 }}
              >
                <div className="code-header">
                  <span className="code-lang">Python</span>
                  <div className="code-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
                <div className="code-content">
                  <div className="code-line">
                    <span className="code-keyword">import</span> <span className="code-module">pandas</span> <span className="code-keyword">as</span> <span className="code-module">pd</span>
                  </div>
                  <div className="code-line">
                    <span className="code-keyword">from</span> <span className="code-module">sklearn</span> <span className="code-keyword">import</span> <span className="code-function">model_selection</span>
                  </div>
                  <div className="code-line">
                    <span className="code-variable">model</span> = <span className="code-function">RandomForest</span>()
                  </div>
                  <div className="code-line">
                    <span className="code-variable">accuracy</span> = <span className="code-number">0.94</span>
                  </div>
                </div>
              </motion.div>

              {/* ML Model Visualization */}
              <motion.div 
                className="ml-visualization"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.8, delay: 0.7 }}
              >
                <div className="neural-network">
                  <div className="network-layer">
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                  </div>
                  <div className="network-layer">
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                  </div>
                  <div className="network-layer">
                    <div className="neuron"></div>
                    <div className="neuron"></div>
                  </div>
                </div>
                <div className="ml-label">Neural Network</div>
              </motion.div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="section-container">
          <motion.h2 
            className="section-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Why Choose Our Platform
          </motion.h2>

          <div className="features-grid">
            <motion.div 
              className="feature-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <div className="feature-icon">üìä</div>
              <h3>Hands-on Projects</h3>
              <p>Apply your skills with real-world projects and build a portfolio that impresses employers.</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <div className="feature-icon">üß†</div>
              <h3>Expert Instructors</h3>
              <p>Learn from industry professionals with years of experience in data science and AI.</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <div className="feature-icon">üöÄ</div>
              <h3>Career Support</h3>
              <p>Get help with resume building, interview preparation, and job placement assistance.</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <div className="feature-icon">üì±</div>
              <h3>Learn Anywhere</h3>
              <p>Access courses on any device and learn at your own pace with our mobile-friendly platform.</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="testimonials-section">
        <div className="section-container">
          <motion.h2 
            className="section-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            What Our Students Say
          </motion.h2>

          <div className="testimonials-grid">
            <motion.div 
              className="testimonial-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <div className="testimonial-content">
                <p>"This platform transformed my career. I went from beginner to data scientist in just 6 months!"</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">üë©‚Äçüíº</div>
                <div className="author-info">
                  <h4>Sarah Johnson</h4>
                  <p>Data Scientist at TechCorp</p>
                </div>
              </div>
            </motion.div>

            <motion.div 
              className="testimonial-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <div className="testimonial-content">
                <p>"The hands-on projects gave me the practical experience I needed to land my dream job."</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">üë®‚Äçüíª</div>
                <div className="author-info">
                  <h4>Michael Chen</h4>
                  <p>ML Engineer at DataWorks</p>
                </div>
              </div>
            </motion.div>

            <motion.div 
              className="testimonial-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <div className="testimonial-content">
                <p>"The instructors are amazing! They make complex concepts easy to understand and apply."</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">üë©‚Äçüéì</div>
                <div className="author-info">
                  <h4>Emily Rodriguez</h4>
                  <p>Data Analyst at InsightCo</p>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="section-container">
          <motion.h2 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Ready to Start Your Data Science Journey?
          </motion.h2>
          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.1 }}
            viewport={{ once: true }}
          >
            Join thousands of students who have transformed their careers with our platform.
          </motion.p>
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <button className="primary-button large" onClick={handleGetStarted}>Get Started Now</button>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
