import React, { useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import './Home.css';
import RoleCard from '../components/RoleCard';
import dsRoles from '../utils/dsRoles';
import { useHistory } from 'react-router-dom';

const TABS = ['Courses', 'Projects', 'Quizzes', 'Articles'];

const buildItems = (type, role) => Array.from({ length: 8 }).map((_, i) => ({
  id: `${role.slug}-${type}-${i + 1}`,
  title: `${type} â€¢ ${role.title} #${i + 1}`,
  description: `A curated ${type.toLowerCase()} for ${role.title}.`,
}));

const Home = () => {
  const history = useHistory();
  const [selectedRole, setSelectedRole] = useState(null);
  const [activeTab, setActiveTab] = useState('Courses');

  const containerVariants = { hidden: { opacity: 0 }, visible: { opacity: 1, transition: { staggerChildren: 0.2 } } };
  const itemVariants = { hidden: { y: 20, opacity: 0 }, visible: { y: 0, opacity: 1, transition: { duration: 0.6 } } };

  const onSelectRole = (role) => {
    if (selectedRole?.slug === role.slug) {
      setSelectedRole(null);
    } else {
      setSelectedRole(role);
      setActiveTab('Courses');
    }
  };

  const previewItems = useMemo(() => {
    if (!selectedRole) return [];
    return buildItems(activeTab.slice(0, -1), selectedRole).slice(0, 3);
  }, [selectedRole, activeTab]);

  const openFullRole = () => {
    if (selectedRole) history.push(`/role/${selectedRole.slug}`);
  };

  return (
    <div className="home-container">
      {/* Hero with animated background and illustrations */}
      <section className="hero-section two-col" style={{ background: 'radial-gradient(1200px 500px at 10% 10%, rgba(99, 102, 241, 0.12), transparent 60%), radial-gradient(900px 400px at 90% 20%, rgba(34, 211, 238, 0.12), transparent 60%), linear-gradient(135deg, #f6f8fc 0%, #eef1f8 100%)' }}>
        <div className="home-background-shapes">
          <motion.div className="shape shape-blue" animate={{ x: [0, 25, 0], y: [0, -15, 0], rotate: [0, 5, 0] }} transition={{ duration: 16, repeat: Infinity, repeatType: 'reverse' }} />
          <motion.div className="shape shape-teal" animate={{ x: [0, -20, 0], y: [0, 20, 0], rotate: [0, -3, 0] }} transition={{ duration: 18, repeat: Infinity, repeatType: 'reverse', delay: 2 }} />
          <motion.div className="shape shape-purple" animate={{ x: [0, 12, -8, 0], y: [0, 8, -8, 0], rotate: [0, 8, -4, 0] }} transition={{ duration: 22, repeat: Infinity, repeatType: 'reverse', delay: 1.2 }} />
        </div>

        <motion.div className="hero-content" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h1 variants={itemVariants} className="hero-title">
            Welcome to your <span>Data Science</span> journey
          </motion.h1>
          <motion.p variants={itemVariants} className="hero-subtitle">
            Pick a role to get a focused learning path. Clean, modern, and consistent with the landing UI.
          </motion.p>
        </motion.div>

        <div className="hero-illustrations">
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="8" y="10" width="104" height="60" rx="10" fill="#ffffff" stroke="#e5e7eb" />
              <polyline points="15,60 35,45 55,50 75,30 95,35" fill="none" stroke="#4f46e5" strokeWidth="3" />
              <circle cx="35" cy="45" r="3" fill="#4f46e5" />
              <circle cx="75" cy="30" r="3" fill="#4f46e5" />
            </svg>
            <span>Insights</span>
          </div>
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="18" y="10" width="84" height="60" rx="8" fill="#ffffff" stroke="#e5e7eb" />
              <rect x="28" y="20" width="24" height="40" rx="4" fill="#a78bfa" />
              <rect x="58" y="30" width="24" height="30" rx="4" fill="#06b6d4" />
            </svg>
            <span>Dashboards</span>
          </div>
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="10" y="20" width="100" height="40" rx="20" fill="#ffffff" stroke="#e5e7eb" />
              <circle cx="40" cy="40" r="10" fill="#6366f1" />
              <rect x="55" y="35" width="40" height="10" rx="5" fill="#94a3b8" />
            </svg>
            <span>AI Systems</span>
          </div>
        </div>
      </section>

      {/* Roles grid */}
      <section className="roles-section">
        <div className="roles-background-shapes">
          <div className="roles-shape rs1"></div>
          <div className="roles-shape rs2"></div>
        </div>

        <motion.div className="roles-header" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h2 className="section-title" variants={itemVariants}>Select a Role to Get Started</motion.h2>
          <motion.p className="section-subtitle" variants={itemVariants}>
            Weâ€™ll show highlights for Courses, Projects, Quizzes, and Articles. Click See More for the full page.
          </motion.p>
        </motion.div>

        <div className="roles-grid">
          {dsRoles.map((role) => (
            <motion.div key={role.id} variants={itemVariants} initial="hidden" whileInView="visible" viewport={{ once: true }}>
              <RoleCard role={role} onClick={() => onSelectRole(role)} />
            </motion.div>
          ))}
        </div>

        {selectedRole && (
          <motion.div className="role-accordion" initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }}>
            <div className="accordion-header">
              <div className="accordion-title">
                <span className="ds-role-icon">{selectedRole.icon}</span>
                <div>
                  <h3>{selectedRole.title}</h3>
                  <p className="muted">A snapshot of recommended content. Explore more on the full page.</p>
                </div>
              </div>

              <div className="tab-bar">
                {TABS.map((tab) => (
                  <button
                    key={tab}
                    className={`tab ${activeTab === tab ? 'active' : ''}`}
                    onClick={() => setActiveTab(tab)}
                  >
                    {tab}
                  </button>
                ))}
              </div>
            </div>

            <div className="accordion-content">
              <div className="carousel">
                {previewItems.map((it) => (
                  <div key={it.id} className="carousel-card">
                    <h4>{it.title}</h4>
                    <p>{it.description}</p>
                  </div>
                ))}
              </div>
              <div className="see-more">
                <button className="btn-outline" onClick={openFullRole}>See More for {selectedRole.title}</button>
              </div>
            </div>
          </motion.div>
        )}
      </section>
    </div>
  );
};

export default Home;
