import { motion } from "framer-motion";
import React from "react";
import { Link } from 'react-router-dom';
import "./LandingPage.css"; // Import associated CSS

const LandingPage = () => {
  // Animation configurations
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
      },
    },
  };

  const buttonHover = {
    hover: {
      scale: 1.05,
      boxShadow: "0 5px 15px rgba(0,0,0,0.1)",
      transition: { duration: 0.2 },
    },
    tap: { scale: 0.98 },
  };

  return (
    <div className="landing-container">
      {/* Background elements */}
      <div className="background-shapes">
        <motion.div
          className="shape shape-blue"
          animate={{
            x: [0, 30, 0],
            y: [0, -20, 0],
            rotate: [0, 5, 0],
          }}
          transition={{
            duration: 15,
            repeat: Infinity,
            repeatType: "reverse",
          }}
        />
        <motion.div
          className="shape shape-teal"
          animate={{
            x: [0, -25, 0],
            y: [0, 25, 0],
            rotate: [0, -3, 0],
          }}
          transition={{
            duration: 18,
            repeat: Infinity,
            repeatType: "reverse",
            delay: 3,
          }}
        />
      </div>

      {/* Hero content */}
      <motion.div
        className="hero-content"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        <motion.h1 className="hero-title" variants={itemVariants}>
          Your Data Science Career Starts Here
        </motion.h1>

        <motion.div className="button-group" variants={itemVariants}>
          <Link className="btn btn-primary" to="/signin" aria-label="Sign in">
            Sign In
          </Link>
          <Link className="btn btn-secondary" to="/signup" aria-label="Sign up">
            Sign Up
          </Link>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default LandingPage;
