import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import roleSkillsData from "../utils/roleSkillsData";
import "./Roadmap.css"; // We'll create this CSS file

const Roadmap = () => {
  const { roleSlug } = useParams();
  const history = useHistory();
  const [roleData, setRoleData] = useState(null);
  const [skillsProgress, setSkillsProgress] = useState({});
  const [showCelebration, setShowCelebration] = useState(false);
  const [completedSkill, setCompletedSkill] = useState(null);
  const [currentSkillIndex, setCurrentSkillIndex] = useState(0);

  // Get all skills in a flat array for the roadmap
  const getAllSkills = (roleData) => {
    if (!roleData) return [];
    const allSkills = [];
    Object.entries(roleData.skills).forEach(([category, skills]) => {
      skills.forEach((skill, index) => {
        allSkills.push({
          ...skill,
          category,
          id: `${category}-${index}`,
          categoryIndex: index
        });
      });
    });
    return allSkills;
  };

  const allSkills = getAllSkills(roleData);
  const completedCount = Object.values(skillsProgress).filter(Boolean).length;
  const airplanePosition = allSkills.length > 0 ? (completedCount / allSkills.length) * 90 + 5 : 5;

  useEffect(() => {
    if (roleSlug && roleSkillsData[roleSlug]) {
      const data = roleSkillsData[roleSlug];
      setRoleData(data);

      // Load progress from localStorage
      const savedProgress = localStorage.getItem(`roadmap-progress-${roleSlug}`);
      if (savedProgress) {
        try {
          const progress = JSON.parse(savedProgress);
          setSkillsProgress(progress);

          // Find the highest completed skill index
          let highestIndex = 0;
          allSkills.forEach((skill, index) => {
            if (progress[skill.id]) {
              highestIndex = index;
            }
          });
          setCurrentSkillIndex(highestIndex);
        } catch (e) {
          console.error("Error loading progress:", e);
        }
      }
    }
  }, [roleSlug]);

  useEffect(() => {
    // Save progress to localStorage
    if (roleSlug) {
      localStorage.setItem(`roadmap-progress-${roleSlug}`, JSON.stringify(skillsProgress));
    }
  }, [skillsProgress, roleSlug]);

  const completeSkill = (skillId) => {
    if (skillsProgress[skillId]) return; // Already completed

    const newProgress = { ...skillsProgress, [skillId]: true };
    setSkillsProgress(newProgress);
    setCompletedSkill(skillId);
    setShowCelebration(true);

    // Update current skill index
    const skillIndex = allSkills.findIndex(skill => skill.id === skillId);
    if (skillIndex > currentSkillIndex) {
      setCurrentSkillIndex(skillIndex);
    }

    // Hide celebration after 3 seconds
    setTimeout(() => {
      setShowCelebration(false);
      setCompletedSkill(null);
    }, 3000);
  };

  const completeNextSkill = () => {
    if (currentSkillIndex < allSkills.length - 1) {
      const nextSkillIndex = currentSkillIndex + 1;
      const nextSkillId = allSkills[nextSkillIndex].id;
      completeSkill(nextSkillId);
      setCurrentSkillIndex(nextSkillIndex);
    }
  };

  const resetProgress = () => {
    setSkillsProgress({});
    setCurrentSkillIndex(0);
    localStorage.removeItem(`roadmap-progress-${roleSlug}`);
  };

  const getSkillColor = (skill, isCompleted) => {
    if (isCompleted) return '#10b981'; // Green for completed
    if (skill.importance === 'Essential') return '#ef4444'; // Red for essential
    if (skill.importance === 'Important') return '#f59e0b'; // Orange for important
    return '#6b7280'; // Gray for useful
  };

  // If no roleSlug, show default page
  if (!roleSlug) {
    return (
      <div className="roadmap-default-container">
        <div className="roadmap-default-content">
          <h1>üõ©Ô∏è</h1>
          <h2>Choose Your Learning Path</h2>
          <p>Select a data science role from the home page to start your personalized learning journey with our interactive roadmap.</p>
          <button onClick={() => window.location.href = '/home'}>
            Explore Roles
          </button>
        </div>
      </div>
    );
  }

  // Loading state
  if (!roleData) {
    return (
      <div className="roadmap-loading">
        <div className="roadmap-loading-spinner"></div>
        <p>Loading roadmap...</p>
      </div>
    );
  }

  // Main roadmap interface
  return (
    <div className="roadmap-container">
      {/* Header */}
      <div className="roadmap-header">
        <div className="roadmap-title-section">
          <div className="roadmap-icon" style={{ background: roleData.gradient }}>
            {roleData.icon}
          </div>
          <div>
            <h1>{roleData.title} Learning Path</h1>
            <p>{roleData.description}</p>
          </div>
        </div>

        <div className="roadmap-progress-stats">
          <div className="progress-stat">
            <div className="progress-number">{allSkills.length}</div>
            <div className="progress-label">Total Skills</div>
          </div>
          <div className="progress-stat">
            <div className="progress-number">{completedCount}</div>
            <div className="progress-label">Completed</div>
          </div>
          <div className="progress-stat">
            <div className="progress-number">
              {Math.round((completedCount / allSkills.length) * 100) || 0}%
            </div>
            <div className="progress-label">Progress</div>
          </div>
        </div>

        <div className="roadmap-controls">
          <button onClick={() => history.goBack()}>
            ‚Üê Back to Skills
          </button>
          <button onClick={resetProgress}>
            Reset Progress
          </button>
        </div>
      </div>

      {/* Roadmap Visualization */}
      <div className="roadmap-visualization">
        <div className="road-path">
          <div className="road-dots"></div>
        </div>

        {/* Background Elements */}
        <div className="roadmap-background">
          <div className="cloud cloud-1"></div>
          <div className="cloud cloud-2"></div>
          <div className="cloud cloud-3"></div>
          <div className="tree tree-1">üå≤</div>
          <div className="tree tree-2">üå≤</div>
          <div className="mountain"></div>
          <div className="mountain mountain-2"></div>
          <div className="mountain mountain-3"></div>
        </div>

        {/* Airplane */}
        <div className="airplane" style={{ left: `${airplanePosition}%` }}>
          ‚úàÔ∏è
        </div>

        {/* Skills as Houses */}
        <div className="skills-houses-container">
          {allSkills.map((skill, index) => {
            const isCompleted = skillsProgress[skill.id];
            const position = (index / (allSkills.length - 1)) * 90 + 5;

            return (
              <div
                key={skill.id}
                className={`skill-house ${isCompleted ? 'completed' : ''}`}
                style={{ left: `${position}%`, bottom: `${50 - Math.sin((index / (allSkills.length - 1)) * Math.PI) * 12}%` }}
                onClick={() => !isCompleted && completeSkill(skill.id)}
                title={`${skill.name} - ${skill.category} (${skill.level}, ${skill.importance})`}
              >
                <div className="house-icon" style={{ background: getSkillColor(skill, isCompleted) }}>
                  {isCompleted ? '‚úÖ' : skill.icon || 'üìö'}
                </div>
                <div className="house-roof" style={{ borderBottomColor: isCompleted ? '#059669' : '#b45309' }}></div>
                <div className="house-body" style={{ background: getSkillColor(skill, isCompleted), color: 'white' }}>
                  {isCompleted ? '‚úÖ' : skill.icon || 'üìö'}
                </div>
                <div className="skill-name" style={{ 
                  background: isCompleted ? '#d1fae5' : '#f3f4f6',
                  color: isCompleted ? '#065f46' : '#374151'
                }}>
                  {skill.name}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Controls */}
      <div className="roadmap-action-controls">
        <button className="btn-primary" onClick={completeNextSkill} disabled={currentSkillIndex >= allSkills.length - 1}>
          Mark Next Skill Complete
        </button>
        <button className="btn-secondary" onClick={resetProgress}>
          Reset Progress
        </button>
      </div>

      {/* Celebration Modal */}
      {showCelebration && completedSkill && (
        <div className="celebration-modal">
          <div className="celebration-content">
            <div className="celebration-icon">üéâ</div>
            <h3>Skill Completed!</h3>
            <p>
              Great job completing{' '}
              <span>
                {allSkills.find(s => s.id === completedSkill)?.name}
              </span>
              !
            </p>
            <div className="celebration-progress">
              {completedCount} of {allSkills.length} skills completed
            </div>
            <div className="progress-bar">
              <div className="progress-fill" style={{ 
                width: `${(completedCount / allSkills.length) * 100}%`,
                background: roleData.gradient
              }}></div>
            </div>
            <button onClick={() => setShowCelebration(false)}>
              Continue Learning
            </button>
          </div>
        </div>
      )}

      {/* Progress Summary */}
      <div className="progress-summary">
        <h3>Learning Progress by Category</h3>
        <div className="category-progress-grid">
          {Object.entries(roleData.skills).map(([category, skills]) => {
            const categoryCompleted = skills.filter((skill, index) => 
              skillsProgress[`${category}-${index}`]
            ).length;
            const categoryProgress = (categoryCompleted / skills.length) * 100;

            return (
              <div key={category} className="category-progress">
                <div className="category-header">
                  <h4>{category}</h4>
                  <span>{categoryCompleted}/{skills.length}</span>
                </div>
                <div className="progress-bar">
                  <div className="progress-fill" style={{
                    width: `${categoryProgress}%`,
                    background: roleData.gradient
                  }}></div>
                </div>
                <div className="progress-percentage">
                  {Math.round(categoryProgress)}% complete
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Roadmap;
