import React from 'react';
import { motion } from 'framer-motion';
import './Home.css';
import RoleCard from '../components/RoleCard';
import dsRoles from '../utils/dsRoles';
import { useHistory } from 'react-router-dom';

const Home = () => {
  const history = useHistory();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.6 } },
  };

  const goToRole = (role) => {
    history.push(`/roles/${role.slug || role.id}`);
  };

  return (
    <div className="home-container">
      {/* Hero Section */}
      <section className="hero-section" style={{ background: 'radial-gradient(900px 400px at 85% 10%, rgba(79,70,229,0.12), transparent 60%), linear-gradient(135deg, #f6f8fc 0%, #eef1f8 100%)' }}>
        <motion.div className="hero-content" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h1 variants={itemVariants} className="hero-title">
            Explore <span>Data Science</span> Roles
          </motion.h1>
          <motion.p variants={itemVariants} className="hero-subtitle">
            Choose a path and follow a curated roadmap tailored to each role.
          </motion.p>
          <motion.div variants={itemVariants} className="hero-buttons">
            <motion.button whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.98 }} className="primary-button">
              Browse Roadmaps
            </motion.button>
            <motion.button whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.98 }} className="secondary-button">
              View Projects
            </motion.button>
          </motion.div>
        </motion.div>
        <div className="hero-graphic">
          <div className="data-points">
            {[...Array(15)].map((_, i) => (
              <motion.div
                key={i}
                className="data-point"
                animate={{ y: [0, Math.random() * 20 - 10], opacity: [0.7, 1, 0.7] }}
                transition={{ duration: 3 + Math.random() * 4, repeat: Infinity, repeatType: 'reverse', delay: Math.random() * 2 }}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Roles Section */}
      <section className="roles-section">
        <motion.div className="roles-header" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h2 className="section-title" variants={itemVariants}>Choose Your Role</motion.h2>
          <motion.p className="section-subtitle" variants={itemVariants}>
            Discover the skills, topics, and projects for each data science career path.
          </motion.p>
        </motion.div>

        <div className="roles-grid">
          {dsRoles.map((role) => (
            <motion.div key={role.id} variants={itemVariants} initial="hidden" whileInView="visible" viewport={{ once: true }}>
              <RoleCard role={role} onClick={() => goToRole(role)} />
            </motion.div>
          ))}
        </div>
      </section>

      {/* Extra Features Section */}
      <section className="features-section">
        <h2 className="section-title">Why Learn With Us</h2>
        <div className="features-grid">
          {[
            { icon: 'ðŸŽ¯', title: 'Personalized Paths', description: 'Adaptive learning tailored to your goals.' },
            { icon: 'ðŸ§ª', title: 'Hands-on Labs', description: 'Practice with real datasets and feedback.' },
            { icon: 'ðŸ“š', title: 'Curated Resources', description: 'Best content from docs, videos, and papers.' },
          ].map((f, idx) => (
            <motion.div key={idx} className="feature-card" whileHover={{ y: -5 }} initial="hidden" animate="visible" variants={itemVariants} transition={{ delay: 0.1 * idx }}>
              <div className="feature-icon">{f.icon}</div>
              <h3>{f.title}</h3>
              <p>{f.description}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="cta-content">
          <h2>Ready to Start Your Role-based Journey?</h2>
          <p>Pick a role, follow the roadmap, and build portfolio-ready projects.</p>
          <motion.button whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.98 }} className="cta-button">
            Start Now
          </motion.button>
        </div>
      </section>
    </div>
  );
};

export default Home;
