import React, { useEffect, useRef, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import roleSkillsData from "../utils/roleSkillsData";
import "./Roadmap.css";

/*
  Roadmap.jsx
  - Uses an SVG S-curve path and samples points along it to place skill cards
  - White dashed road with glow, airplane follows progress
  - Keeps progress in localStorage keyed by roleSlug
  - Expects roleSkillsData[roleSlug].skills in shape:
     { categoryName: [ { name, level, importance, icon? }, ... ] }
*/

const Roadmap = () => {
  const { roleSlug } = useParams();
  const history = useHistory();
  const [roleData, setRoleData] = useState(null);
  const [skillsProgress, setSkillsProgress] = useState({});
  const [showCelebration, setShowCelebration] = useState(false);
  const [completedSkill, setCompletedSkill] = useState(null);

  const svgRef = useRef(null);
  const pathRef = useRef(null);
  const [sampledPoints, setSampledPoints] = useState([]);
  const containerRef = useRef(null);

  // Build flat skills array with stable ids
  const getAllSkills = (data) => {
    if (!data || !data.skills) return [];
    const all = [];
    Object.entries(data.skills).forEach(([category, skills]) => {
      skills.forEach((s, idx) => {
        all.push({
          ...s,
          category,
          id: `${category.replace(/\s+/g, "-").toLowerCase()}-${idx}`,
          indexInCategory: idx,
        });
      });
    });
    return all;
  };

  const allSkills = getAllSkills(roleData);
  const completedCount = Object.values(skillsProgress).filter(Boolean).length;
  const progressRatio = allSkills.length ? completedCount / allSkills.length : 0;

  // Load role data + saved progress
  useEffect(() => {
    if (roleSlug && roleSkillsData[roleSlug]) {
      const data = roleSkillsData[roleSlug];
      setRoleData(data);

      const saved = localStorage.getItem(`roadmap-progress-${roleSlug}`);
      if (saved) {
        try {
          setSkillsProgress(JSON.parse(saved));
        } catch (e) {
          console.warn("Invalid saved progress", e);
        }
      }
    }
  }, [roleSlug]);

  useEffect(() => {
    if (roleSlug) {
      localStorage.setItem(`roadmap-progress-${roleSlug}`, JSON.stringify(skillsProgress));
    }
  }, [skillsProgress, roleSlug]);

  // When roleData or container resizes, sample points along the SVG path
  useEffect(() => {
    const sample = () => {
      if (!pathRef.current) return;
      const path = pathRef.current;
      const length = path.getTotalLength();
      const points = [];

      // We'll sample exactly N points equal to number of skills, but distribute across
      const N = Math.max(2, allSkills.length);
      // Add margin at start and end to keep skills inside view (10% padding)
      for (let i = 0; i < N; i++) {
        // Use eased distribution so spacing looks nice (more outer spacing)
        const t = i / (N - 1); // 0..1
        // Slight S-curve easing
        const eased = 0.03 + 0.94 * t; // keeps tiny padding
        const point = path.getPointAtLength(eased * length);
        points.push({ x: point.x, y: point.y, t });
      }
      setSampledPoints(points);
    };

    // sample after next paint (DOM available)
    setTimeout(sample, 60);

    // Also resample on window resize
    const onResize = () => setTimeout(sample, 80);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [roleData, allSkills.length]);

  // Complete a skill (toggle)
  const completeSkill = (skillId) => {
    // if already completed, do nothing
    if (skillsProgress[skillId]) return;
    const updated = { ...skillsProgress, [skillId]: true };
    setSkillsProgress(updated);
    setCompletedSkill(skillId);
    setShowCelebration(true);
    setTimeout(() => {
      setShowCelebration(false);
      setCompletedSkill(null);
    }, 2200);
  };

  // Mark next skill complete (by index)
  const completeNextSkill = () => {
    const nextIdx = allSkills.findIndex(s => !skillsProgress[s.id]);
    if (nextIdx === -1) return;
    completeSkill(allSkills[nextIdx].id);
  };

  const resetProgress = () => {
    setSkillsProgress({});
    localStorage.removeItem(`roadmap-progress-${roleSlug}`);
  };

  // helpers for icon and colors
  const getSkillIcon = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("sql")) return "üóÑÔ∏è";
    if (n.includes("python")) return "üêç";
    if (n.includes("clean")) return "üßπ";
    if (n.includes("visual") || n.includes("plot")) return "üìä";
    if (n.includes("stat")) return "üìà";
    if (n.includes("machine") || n.includes("ml")) return "ü§ñ";
    if (n.includes("deep")) return "üß†";
    if (n.includes("cloud")) return "‚òÅÔ∏è";
    if (n.includes("big")) return "üóÉÔ∏è";
    return "üéØ";
  };
  const getSkillColor = (skill, completed) => {
    if (completed) return "linear-gradient(90deg,#059669,#10b981)"; // completed green
    if (skill.importance === "Essential") return "linear-gradient(90deg,#ef4444,#f97316)"; // essential red-orange
    if (skill.importance === "Important") return "linear-gradient(90deg,#f59e0b,#fbbf24)"; // amber
    return "linear-gradient(90deg,#3b82f6,#6366f1)"; // blue
  };

  // If no roleSlug provided -> default screen
  if (!roleSlug) {
    return (
      <div className="roadmap-default-container">
        <div className="roadmap-default-content">
          <h1>üõ©Ô∏è</h1>
          <h2>Choose Your Learning Path</h2>
          <p>Select a role from the home page to start a beautiful interactive roadmap with progress tracking.</p>
          <button onClick={() => window.location.href = "/home"}>Explore Roles</button>
        </div>
      </div>
    );
  }

  // Loading state while roleData loads
  if (!roleData) {
    return (
      <div className="roadmap-loading">
        <div className="roadmap-loading-spinner" />
        <p>Loading roadmap...</p>
      </div>
    );
  }

  // compute airplane position using sampledPoints and progressRatio
  const airplanePos = (() => {
    if (!sampledPoints.length) return { left: "6%", top: "50%" };
    // Use a targeted index by progressRatio
    const idxFloat = progressRatio * (sampledPoints.length - 1);
    const idx = Math.min(sampledPoints.length - 1, Math.max(0, Math.round(idxFloat)));
    const p = sampledPoints[idx];
    // convert svg coords to container percent
    const svg = svgRef.current;
    if (!svg) return { left: `${p.x}px`, top: `${p.y}px` };
    const bbox = svg.getBoundingClientRect();
    return {
      left: `${((p.x - svg.viewBox.baseVal.x) / svg.viewBox.baseVal.width) * 100}%`,
      top: `${((p.y - svg.viewBox.baseVal.y) / svg.viewBox.baseVal.height) * 100}%`,
    };
  })();

  return (
    <div className="roadmap-container">
      <div className="roadmap-header">
        <div className="roadmap-title-section">
          <div className="roadmap-icon" style={{ background: roleData.gradient || "linear-gradient(90deg,#3b82f6,#6366f1)" }}>
            {roleData.icon || "üìö"}
          </div>
          <div>
            <h1>{roleData.title || "Role"} Learning Path</h1>
            <p>{roleData.description || "An interactive roadmap to guide your learning."}</p>
          </div>
        </div>

        <div className="roadmap-progress-stats">
          <div className="progress-stat">
            <div className="progress-number">{allSkills.length}</div>
            <div className="progress-label">Total Skills</div>
          </div>
          <div className="progress-stat">
            <div className="progress-number">{completedCount}</div>
            <div className="progress-label">Completed</div>
          </div>
          <div className="progress-stat">
            <div className="progress-number">{Math.round(progressRatio * 100) || 0}%</div>
            <div className="progress-label">Progress</div>
          </div>
        </div>

        <div className="roadmap-controls">
          <button onClick={() => history.goBack()}>‚Üê Back</button>
          <button onClick={resetProgress}>Reset Progress</button>
        </div>
      </div>

      {/* Visualization */}
      <div className="roadmap-visualization" ref={containerRef}>
        {/* background soft circles */}
        <div className="roadmap-bg-el bg-circle" />
        <div className="roadmap-bg-el bg-circle-2" />

        {/* SVG road */}
        <div className="road-svg-wrap" aria-hidden>
          <svg
            ref={svgRef}
            viewBox="0 0 1200 640"
            preserveAspectRatio="xMidYMid meet"
            width="100%"
            height="100%"
          >
            <defs>
              {/* blur + glow filter */}
              <filter id="blurGlow" x="-40%" y="-40%" width="180%" height="180%">
                <feGaussianBlur stdDeviation="14" result="coloredBlur" />
                <feMerge>
                  <feMergeNode in="coloredBlur" />
                  <feMergeNode in="SourceGraphic" />
                </feMerge>
              </filter>

              <linearGradient id="roadGrad" x1="0" x2="1">
                <stop offset="0" stopColor="#ffffff" stopOpacity="0.98" />
                <stop offset="1" stopColor="#ffffff" stopOpacity="0.95" />
              </linearGradient>
            </defs>

            {/* A wider under-glow stroke for soft shadow */}
            <path
              ref={pathRef}
              d="M 80 420
                 C 220 320, 340 220, 520 220
                 C 700 220, 760 420, 940 340
                 C 1040 300, 1120 220, 1180 160"
              className="road-under"
              fill="none"
              strokeLinecap="round"
            />

            {/* main thick white road with subtle outline */}
            <path
              d="M 80 420
                 C 220 320, 340 220, 520 220
                 C 700 220, 760 420, 940 340
                 C 1040 300, 1120 220, 1180 160"
              className="road-main road-glow"
              stroke="url(#roadGrad)"
              fill="none"
              strokeLinecap="round"
            />

            {/* dashed inner path */}
            <path
              d="M 80 420
                 C 220 320, 340 220, 520 220
                 C 700 220, 760 420, 940 340
                 C 1040 300, 1120 220, 1180 160"
              className="road-dash"
              fill="none"
              strokeLinecap="round"
            />
          </svg>
        </div>

        {/* Airplane that follows progress */}
        <div
          className="airplane"
          style={{
            left: airplanePos.left,
            top: airplanePos.top,
          }}
          aria-hidden
        >
          ‚úàÔ∏è
        </div>

        {/* Skill cards placed along sampledPoints */}
        <div className="skills-houses-container">
          {allSkills.map((skill, idx) => {
            const completed = !!skillsProgress[skill.id];
            const locked = skill.locked === true; // if you flag locked in data
            // find corresponding sampled point (fallback to distribution if not ready)
            const point = sampledPoints[idx] || {
              x: 60 + (idx / Math.max(1, allSkills.length - 1)) * 1080,
              y: 220 + Math.sin((idx / Math.max(1, allSkills.length - 1)) * Math.PI) * -60,
            };

            // convert svg coordinates into percent; uses svg viewBox
            const svg = svgRef.current;
            let left = "10%";
            let top = "50%";
            if (svg) {
              const v = svg.viewBox.baseVal;
              left = `${((point.x - v.x) / v.width) * 100}%`;
              top = `${((point.y - v.y) / v.height) * 100}%`;
            } else {
              // fallback when sampling not ready
              left = `${10 + (idx / Math.max(1, allSkills.length - 1)) * 82}%`;
              top = `${48 - Math.sin((idx / Math.max(1, allSkills.length - 1)) * Math.PI) * 12}%`;
            }

            // color for icon circle
            const iconStyle = { background: completed ? undefined : getSkillColor(skill, false) };

            return (
              <div
                key={skill.id}
                className={`skill-card ${completed ? "completed" : (locked ? "locked" : "available")}`}
                style={{ left, top }}
                onClick={(e) => {
                  e.stopPropagation();
                  if (locked) return;
                  if (!completed) completeSkill(skill.id);
                }}
                title={`${skill.name} ‚Äî ${skill.category} (${skill.level || "N/A"})`}
                role="button"
              >
                {/* lock badge */}
                {locked ? <div className="skill-lock">üîí</div> : null}

                <div
                  className="skill-icon"
                  style={{
                    background: completed ? undefined : getSkillColor(skill, false),
                  }}
                >
                  {completed ? "‚úÖ" : (skill.icon || getSkillIcon(skill.name))}
                </div>

                <div className="skill-name">{skill.name}</div>
                <div className="skill-sub">{skill.level || skill.importance || ""}</div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Controls */}
      <div className="roadmap-action-controls">
        <button className="btn-primary" onClick={completeNextSkill} disabled={completedCount >= allSkills.length}>
          Mark Next Skill Complete
        </button>
        <button className="btn-secondary" onClick={resetProgress}>Reset Progress</button>
      </div>

      {/* Celebration */}
      {showCelebration && completedSkill && (
        <div className="celebration-modal" onClick={() => setShowCelebration(false)}>
          <div className="celebration-content" onClick={(e)=>e.stopPropagation()}>
            <div style={{ fontSize:44 }}>üéâ</div>
            <h3>Skill Completed!</h3>
            <p>You finished <strong>{allSkills.find(s => s.id === completedSkill)?.name}</strong></p>
            <div style={{ marginTop: 8, marginBottom: 8 }}>
              {completedCount} of {allSkills.length} completed
            </div>
            <button className="btn-primary" onClick={() => setShowCelebration(false)}>Continue</button>
          </div>
        </div>
      )}

      {/* Progress summary */}
      <div className="progress-summary">
        <h3>Learning Progress by Category</h3>
        <div className="category-progress-grid">
          {Object.entries(roleData.skills).map(([category, skills]) => {
            const catCompleted = skills.filter((s, i) => !!skillsProgress[`${category.replace(/\s+/g,"-").toLowerCase()}-${i}`]).length;
            const percent = Math.round((catCompleted / Math.max(1, skills.length)) * 100);
            return (
              <div key={category} style={{ background: "transparent", padding: 10, borderRadius: 10 }}>
                <div style={{ display:"flex", justifyContent:"space-between", alignItems:"center", marginBottom:6 }}>
                  <strong style={{ fontSize:15 }}>{category}</strong>
                  <span style={{ color:"#64748b" }}>{catCompleted}/{skills.length}</span>
                </div>
                <div style={{ height:10, background:"#eef2ff", borderRadius:999, overflow:"hidden" }}>
                  <div style={{ width:`${percent}%`, height:"100%", background: roleData.gradient || "#60a5fa" }} />
                </div>
                <div style={{ marginTop:6, color:"#64748b", fontSize:13 }}>{percent}% complete</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Roadmap;
