import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useHistory } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const [theme, setTheme] = useState('dark');

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'dark' ? 'light' : 'dark'));
  };

  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);
  const history = useHistory();

  const handleStartLearning = () => {
    history.push('/signup');
  };

  const handleGetStarted = () => {
    history.push('/signup');
  };

  const handleSignIn = () => {
    history.push('/signin');
  };

  const handleSignUp = () => {
    history.push('/signup');
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.2 },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.6 } },
  };

  return (
    <div className={`landing-page ${theme}-theme`}>
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-container">
          {/* Logo */}
          <div className="navbar-logo">
            <div className="logo-container">
              <div className="logo-icon">
                <svg width="40" height="40" viewBox="0 0 40 40" fill="none">
                  <defs>
                    <linearGradient id="logoGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                      <stop offset="0%" stopColor="#6366f1" />
                      <stop offset="50%" stopColor="#8b5cf6" />
                      <stop offset="100%" stopColor="#06b6d4" />
                    </linearGradient>
                  </defs>
                  <circle cx="20" cy="20" r="18" fill="url(#logoGradient)" fillOpacity="0.1" stroke="url(#logoGradient)" strokeWidth="2"/>
                  <path d="M12 16L20 24L28 16" stroke="url(#logoGradient)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                  <circle cx="15" cy="12" r="2" fill="url(#logoGradient)"/>
                  <circle cx="25" cy="12" r="2" fill="url(#logoGradient)"/>
                  <circle cx="20" cy="28" r="2" fill="url(#logoGradient)"/>
                  <path d="M8 20H12M28 20H32" stroke="url(#logoGradient)" strokeWidth="2" strokeLinecap="round"/>
                </svg>
              </div>
              <div className="logo-text">
                <span className="logo-name">PR Learning</span>
                <span className="logo-tagline">Platform</span>
              </div>
            </div>
          </div>

          {/* Navigation Buttons */}
          <div className="navbar-buttons">
            <button className="nav-button theme-toggle-btn" onClick={toggleTheme}>
              {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
            </button>
            <button className="nav-button signin-btn" onClick={handleSignIn}>
              Sign In
            </button>
            <button className="nav-button signup-btn" onClick={handleSignUp}>
              Sign Up
            </button>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero-section">
        {/* Background image */}
        <div className="hero-background">
          <img 
            src="/landing.jpeg"   // <-- place your background image in public/images
            alt="Data Science Background"
            className="hero-bg-image"
          />
        </div>

        <div className="hero-content-container">
          <motion.div 
            className="hero-content" 
            initial="hidden" 
            animate="visible" 
            variants={containerVariants}
          >
            <motion.h1 variants={itemVariants} className="hero-title">
              Master Data Science
            </motion.h1>

            <motion.h2 variants={itemVariants} className="hero-subtitle">
              Transform Your Future
            </motion.h2>

            <motion.p variants={itemVariants} className="hero-description">
              Learn Python, Machine Learning, AI, and Data Analytics through hands-on projects.
              Build real-world skills that employers demand.
            </motion.p>

            <motion.div variants={itemVariants} className="hero-cta">
              <button className="primary-button" onClick={handleStartLearning}>Start Learning Free</button>
              <button className="secondary-button">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" stroke="currentColor" strokeWidth="2"/>
                  <path d="M10 8L16 12L10 16V8Z" stroke="currentColor" strokeWidth="2"/>
                </svg>
                Watch Learning Path
              </button>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="section-container">
          <h2 className="section-title">Why Choose Our Platform</h2>

          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">üìä</div>
              <h3>Hands-on Projects</h3>
              <p>Apply your skills with real-world projects and build a portfolio that impresses employers.</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">üß†</div>
              <h3>Expert Instructors</h3>
              <p>Learn from industry professionals with years of experience in data science and AI.</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">üöÄ</div>
              <h3>Career Support</h3>
              <p>Get help with resume building, interview preparation, and job placement assistance.</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">üì±</div>
              <h3>Learn Anywhere</h3>
              <p>Access courses on any device and learn at your own pace with our mobile-friendly platform.</p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="section-container">
          <h2>Ready to Start Your Data Science Journey?</h2>
          <p>Join thousands of students who have transformed their careers with our platform.</p>
          <button className="primary-button large" onClick={handleGetStarted}>Get Started Now</button>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
