import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import api from '../utils/api';
import './RoleDetails.css';

const RoleDetails = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRoleDetails = async () => {
      try {
        const response = await api.get(`/api/roles/${slug}`);
        setRole(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch role details');
      } finally {
        setLoading(false);
      }
    };

    fetchRoleDetails();
  }, [slug]);

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;
  if (!role) return <div className="not-found">Role not found</div>;

  return (
    <motion.div 
      className="role-details-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      style={{ '--role-color': role.color, '--role-gradient': role.gradient }}
    >
      {/* Header Section */}
      <div className="role-header">
        <button onClick={() => navigate(-1)} className="back-button">
          &larr; Back to Roles
        </button>
        <div className="role-title-container">
          <h1 className="role-title">{role.title}</h1>
          <span className="role-category">{role.category}</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="role-content-grid">
        {/* Overview Section */}
        <div className="overview-section">
          <h2>Overview</h2>
          <p>{role.description}</p>

          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-value">${role.salary?.average || '90,000'}</div>
              <div className="stat-label">Average Salary</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{role.growth || '27%'}</div>
              <div className="stat-label">Job Growth (2020-2030)</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{role.difficulty || 'Medium'}</div>
              <div className="stat-label">Difficulty Level</div>
            </div>
          </div>
        </div>

        {/* Skills Section */}
        <div className="skills-section">
          <h2>Core Skills Required</h2>
          <div className="skills-grid">
            {role.skills?.map((skill, index) => (
              <div key={index} className="skill-card">
                <div className="skill-icon">{skill.icon || 'ðŸ“Š'}</div>
                <h3 className="skill-name">{skill.name}</h3>
                <p className="skill-description">{skill.description}</p>
                <div className="skill-level">
                  <span>Proficiency: </span>
                  <div className="level-bars">
                    {[1, 2, 3, 4, 5].map((level) => (
                      <div 
                        key={level} 
                        className={`level-bar ${level <= skill.level ? 'filled' : ''}`}
                      ></div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Roadmap Section */}
        <div className="roadmap-section">
          <h2>Learning Roadmap</h2>
          <div className="roadmap-stages">
            {role.roadmap?.map((stage, index) => (
              <div key={index} className="roadmap-stage">
                <div className="stage-header">
                  <div className="stage-number">{index + 1}</div>
                  <h3 className="stage-title">{stage.title}</h3>
                </div>
                <ul className="stage-items">
                  {stage.items.map((item, itemIndex) => (
                    <li key={itemIndex} className="stage-item">
                      <input type="checkbox" id={`item-${index}-${itemIndex}`} />
                      <label htmlFor={`item-${index}-${itemIndex}`}>{item}</label>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>

        {/* Resources Section */}
        <div className="resources-section">
          <h2>Recommended Resources</h2>
          <div className="resources-list">
            {role.resources?.map((resource, index) => (
              <a 
                key={index} 
                href={resource.url} 
                target="_blank" 
                rel="noopener noreferrer"
                className="resource-card"
              >
                <div className="resource-type">{resource.type}</div>
                <h3 className="resource-title">{resource.title}</h3>
                <p className="resource-description">{resource.description}</p>
              </a>
            ))}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default RoleDetails;
