import React from 'react';
import { motion } from 'framer-motion';
import './Home.css';
import RoleCard from '../components/RoleCard';
import dsRoles from '../utils/dsRoles';
import { useHistory } from 'react-router-dom';

const Home = () => {
  const history = useHistory();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1, 
      transition: { 
        staggerChildren: 0.2,
        delayChildren: 0.3
      } 
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1, 
      transition: { 
        duration: 0.6,
        ease: [0.6, -0.05, 0.01, 0.99]
      } 
    }
  };

  const goToRole = (role) => {
    history.push(`/role/${role.slug}`);
  };

  return (
    <div className="home-container">
      {/* Original Hero Section */}
      <section className="hero-section two-col" style={{ background: 'radial-gradient(1200px 500px at 10% 10%, rgba(99, 102, 241, 0.12), transparent 60%), radial-gradient(900px 400px at 90% 20%, rgba(34, 211, 238, 0.12), transparent 60%), linear-gradient(135deg, #f6f8fc 0%, #eef1f8 100%)' }}>
        <div className="home-background-shapes">
          <motion.div className="shape shape-blue" animate={{ x: [0, 25, 0], y: [0, -15, 0], rotate: [0, 5, 0] }} transition={{ duration: 16, repeat: Infinity, repeatType: 'reverse' }} />
          <motion.div className="shape shape-teal" animate={{ x: [0, -20, 0], y: [0, 20, 0], rotate: [0, -3, 0] }} transition={{ duration: 18, repeat: Infinity, repeatType: 'reverse', delay: 2 }} />
          <motion.div className="shape shape-purple" animate={{ x: [0, 12, -8, 0], y: [0, 8, -8, 0], rotate: [0, 8, -4, 0] }} transition={{ duration: 22, repeat: Infinity, repeatType: 'reverse', delay: 1.2 }} />
        </div>

        <motion.div className="hero-content" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h1 variants={itemVariants} className="hero-title">
            Welcome to your <span>Data Science</span> journey
          </motion.h1>
          <motion.p variants={itemVariants} className="hero-subtitle">
            Pick a role to get a focused learning path. Clean, modern, and consistent with the landing UI.
          </motion.p>
        </motion.div>

        <div className="hero-illustrations">
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="8" y="10" width="104" height="60" rx="10" fill="#ffffff" stroke="#e5e7eb" />
              <polyline points="15,60 35,45 55,50 75,30 95,35" fill="none" stroke="#4f46e5" strokeWidth="3" />
              <circle cx="35" cy="45" r="3" fill="#4f46e5" />
              <circle cx="75" cy="30" r="3" fill="#4f46e5" />
            </svg>
            <span>Visualizations</span>
          </div>
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="18" y="10" width="84" height="60" rx="8" fill="#ffffff" stroke="#e5e7eb" />
              <rect x="28" y="20" width="24" height="40" rx="4" fill="#a78bfa" />
              <rect x="58" y="30" width="24" height="30" rx="4" fill="#06b6d4" />
            </svg>
            <span>Dashboards</span>
          </div>
          <div className="ill-card">
            <svg viewBox="0 0 120 80" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <rect x="10" y="20" width="100" height="40" rx="20" fill="#ffffff" stroke="#e5e7eb" />
              <circle cx="40" cy="40" r="10" fill="#6366f1" />
              <rect x="55" y="35" width="40" height="10" rx="5" fill="#94a3b8" />
            </svg>
            <span>AI Systems</span>
          </div>
        </div>
      </section>

      {/* Enhanced Roles Section */}
      <section className="roles-section">
        <div className="roles-container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>Discover Your Path</h2>
            <p>Select a role to explore tailored learning journeys</p>
          </motion.div>

          <div className="roles-grid">
            {dsRoles.map((role) => (
              <motion.div 
                key={role.id}
                className="role-card-wrapper"
                initial={{ opacity: 0, scale: 0.95 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.4, delay: 0.1 * role.id }}
                viewport={{ once: true }}
                whileHover={{ 
                  y: -8,
                  transition: { duration: 0.2 }
                }}
              >
                <RoleCard role={role} onClick={() => goToRole(role)} />
              </motion.div>
            ))}
          </div>

          <motion.div
            className="section-footer"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            viewport={{ once: true }}
          >
            <p>Not sure where to start?</p>
            <button className="outline-button">Take Career Quiz</button>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
