import React, { useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import './Home.css';
import RoleCard from '../components/RoleCard';
import dsRoles from '../utils/dsRoles';
import { useHistory } from 'react-router-dom';

const TABS = ['Courses', 'Projects', 'Quizzes', 'Articles'];

const buildItems = (type, role) => Array.from({ length: 8 }).map((_, i) => ({
  id: `${role.slug}-${type}-${i + 1}`,
  title: `${type} • ${role.title} #${i + 1}`,
  description: `A curated ${type.toLowerCase()} for ${role.title}.`,
}));

const Home = () => {
  const history = useHistory();
  const [selectedRole, setSelectedRole] = useState(null);
  const [activeTab, setActiveTab] = useState('Courses');

  const containerVariants = { hidden: { opacity: 0 }, visible: { opacity: 1, transition: { staggerChildren: 0.2 } } };
  const itemVariants = { hidden: { y: 20, opacity: 0 }, visible: { y: 0, opacity: 1, transition: { duration: 0.6 } } };

  const onSelectRole = (role) => {
    if (selectedRole?.slug === role.slug) {
      setSelectedRole(null);
    } else {
      setSelectedRole(role);
      setActiveTab('Courses');
    }
  };

  const previewItems = useMemo(() => {
    if (!selectedRole) return [];
    return buildItems(activeTab.slice(0, -1), selectedRole).slice(0, 3);
  }, [selectedRole, activeTab]);

  const openFullRole = () => {
    if (selectedRole) history.push(`/role/${selectedRole.slug}`);
  };

  return (
    <div className="home-container">
      {/* Hero Section */}
      <section className="hero-section" style={{ background: 'radial-gradient(900px 400px at 85% 10%, rgba(79,70,229,0.12), transparent 60%), linear-gradient(135deg, #f6f8fc 0%, #eef1f8 100%)' }}>
        <motion.div className="hero-content" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h1 variants={itemVariants} className="hero-title">
            Choose Your <span>Data Science</span> Role
          </motion.h1>
          <motion.p variants={itemVariants} className="hero-subtitle">
            Simple start: pick a role. We reveal focused, role-based learning next.
          </motion.p>
        </motion.div>
        <div className="hero-graphic">
          <div className="data-points">
            {[...Array(15)].map((_, i) => (
              <motion.div
                key={i}
                className="data-point"
                animate={{ y: [0, Math.random() * 20 - 10], opacity: [0.7, 1, 0.7] }}
                transition={{ duration: 3 + Math.random() * 4, repeat: Infinity, repeatType: 'reverse', delay: Math.random() * 2 }}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Roles Section */}
      <section className="roles-section">
        <motion.div className="roles-header" initial="hidden" animate="visible" variants={containerVariants}>
          <motion.h2 className="section-title" variants={itemVariants}>Select a Role to Get Started</motion.h2>
          <motion.p className="section-subtitle" variants={itemVariants}>
            We’ll show highlights for Courses, Projects, Quizzes, and Articles. Click See More for the full page.
          </motion.p>
        </motion.div>

        <div className="roles-grid">
          {dsRoles.map((role) => (
            <motion.div key={role.id} variants={itemVariants} initial="hidden" whileInView="visible" viewport={{ once: true }}>
              <RoleCard role={role} onClick={() => onSelectRole(role)} />
            </motion.div>
          ))}
        </div>

        {selectedRole && (
          <motion.div className="role-accordion" initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }}>
            <div className="accordion-header">
              <div className="accordion-title">
                <span className="ds-role-icon">{selectedRole.icon}</span>
                <div>
                  <h3>{selectedRole.title}</h3>
                  <p className="muted">A snapshot of recommended content. Explore more on the full page.</p>
                </div>
              </div>

              <div className="tab-bar">
                {TABS.map((tab) => (
                  <button
                    key={tab}
                    className={`tab ${activeTab === tab ? 'active' : ''}`}
                    onClick={() => setActiveTab(tab)}
                  >
                    {tab}
                  </button>
                ))}
              </div>
            </div>

            <div className="accordion-content">
              <div className="carousel">
                {previewItems.map((it) => (
                  <div key={it.id} className="carousel-card">
                    <h4>{it.title}</h4>
                    <p>{it.description}</p>
                  </div>
                ))}
              </div>
              <div className="see-more">
                <button className="btn-outline" onClick={openFullRole}>See More for {selectedRole.title}</button>
              </div>
            </div>
          </motion.div>
        )}
      </section>
    </div>
  );
};

export default Home;
