// frontend/src/pages/RoleDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { motion } from 'framer-motion';
import './RoleDetails.css';

const RoleDetails = () => {
  const { slug } = useParams();
  const history = useHistory();
  const [role, setRole] = useState(null);
  const [loading, setLoading] = useState(true);

  // This would normally come from your API
  const rolesData = [
    {
      id: 1,
      title: "Data Scientist",
      slug: "data-scientist",
      category: "Machine Learning",
      color: "#6366f1",
      gradient: "linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)",
      icon: "ðŸ§ ",
      description: "Build predictive models and extract insights from complex datasets to drive business decisions.",
      roadmap: [
        {
          stage: "Fundamentals",
          skills: [
            { 
              name: "Python Programming", 
              level: "Advanced", 
              resources: 12,
              description: "Master Python syntax, data structures, and object-oriented programming",
              topics: ["Variables & Data Types", "Functions", "Classes & OOP", "Error Handling"]
            },
            { 
              name: "Statistics", 
              level: "Intermediate", 
              resources: 8,
              description: "Understand statistical concepts essential for data analysis",
              topics: ["Probability", "Distributions", "Hypothesis Testing", "Regression"]
            },
            { 
              name: "SQL", 
              level: "Intermediate", 
              resources: 6,
              description: "Learn to query and manipulate databases",
              topics: ["SELECT queries", "JOINs", "Subqueries", "Indexing"]
            }
          ]
        },
        {
          stage: "Data Analysis",
          skills: [
            { 
              name: "Pandas & NumPy", 
              level: "Advanced", 
              resources: 15,
              description: "Data manipulation and numerical computing in Python",
              topics: ["DataFrames", "Vectorized Operations", "Grouping", "Time Series"]
            },
            { 
              name: "Data Visualization", 
              level: "Intermediate", 
              resources: 10,
              description: "Create effective visualizations to communicate insights",
              topics: ["Matplotlib", "Seaborn", "Plotly", "Dashboard Design"]
            },
            { 
              name: "Exploratory Data Analysis", 
              level: "Intermediate", 
              resources: 8,
              description: "Systematically analyze datasets to summarize characteristics",
              topics: ["Missing Data", "Outliers", "Feature Correlation", "Summary Stats"]
            }
          ]
        },
        {
          stage: "Machine Learning",
          skills: [
            { 
              name: "Supervised Learning", 
              level: "Advanced", 
              resources: 15,
              description: "Learn algorithms that learn from labeled training data",
              topics: ["Linear Regression", "Decision Trees", "SVM", "Ensemble Methods"]
            },
            { 
              name: "Unsupervised Learning", 
              level: "Intermediate", 
              resources: 10,
              description: "Discover patterns in unlabeled data",
              topics: ["Clustering", "Dimensionality Reduction", "Anomaly Detection"]
            },
            { 
              name: "Model Evaluation", 
              level: "Intermediate", 
              resources: 8,
              description: "Assess and compare model performance",
              topics: ["Metrics", "Cross-Validation", "Hyperparameter Tuning"]
            }
          ]
        },
        {
          stage: "Advanced Topics",
          skills: [
            { 
              name: "Deep Learning", 
              level: "Beginner", 
              resources: 12,
              description: "Introduction to neural networks and deep learning",
              topics: ["Neural Networks", "TensorFlow/PyTorch", "CNNs", "RNNs"]
            },
            { 
              name: "Big Data Tools", 
              level: "Beginner", 
              resources: 8,
              description: "Work with large datasets using distributed computing",
              topics: ["Spark", "Hadoop", "Dask", "Cloud Platforms"]
            },
            { 
              name: "Deployment", 
              level: "Beginner", 
              resources: 6,
              description: "Put models into production",
              topics: ["APIs", "Containers", "Monitoring", "CI/CD"]
            }
          ]
        }
      ]
    },
    // Other roles would go here...
  ];

  useEffect(() => {
    const foundRole = rolesData.find(r => r.slug === slug);
    if (foundRole) {
      setRole(foundRole);
    } else {
      history.push('/');
    }
    setLoading(false);
  }, [slug, history]);

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (!role) return <div>Role not found</div>;

  return (
    <div className="role-details-container" style={{ '--role-color': role.color }}>
      <motion.div 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="role-header"
        style={{ background: role.gradient }}
      >
        <div className="header-content">
          <button onClick={() => history.goBack()} className="back-button">
            &larr; Back to Roles
          </button>
          <div className="role-icon">{role.icon}</div>
          <h1>{role.title}</h1>
          <p className="role-category">{role.category}</p>
          <p className="role-description">{role.description}</p>
        </div>
      </motion.div>

      <div className="roadmap-container">
        <div className="roadmap-header">
          <h2>Learning Roadmap</h2>
          <p>Follow this structured path to master {role.title} skills</p>
        </div>

        <div className="timeline">
          {role.roadmap.map((stage, index) => (
            <div key={index} className="timeline-stage">
              <div className="stage-header">
                <div className="stage-marker" style={{ backgroundColor: role.color }}></div>
                <h3>{stage.stage}</h3>
              </div>
              <div className="skills-container">
                {stage.skills.map((skill, skillIndex) => (
                  <motion.div
                    key={skillIndex}
                    className="skill-card"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: skillIndex * 0.1 }}
                    whileHover={{ scale: 1.03 }}
                  >
                    <div className="skill-header">
                      <h4>{skill.name}</h4>
                      <span className={`skill-level ${skill.level.toLowerCase()}`}>
                        {skill.level}
                      </span>
                    </div>
                    <p className="skill-description">{skill.description}</p>
                    <div className="skill-topics">
                      <h5>Key Topics:</h5>
                      <ul>
                        {skill.topics.map((topic, topicIndex) => (
                          <li key={topicIndex}>{topic}</li>
                        ))}
                      </ul>
                    </div>
                    <div className="skill-footer">
                      <span className="resources-count">
                        {skill.resources} resources available
                      </span>
                      <button 
                        className="view-resources-btn"
                        style={{ backgroundColor: role.color }}
                      >
                        View Resources
                      </button>
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RoleDetails;
